    private void addbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addbtnActionPerformed
        if (size < 10) {
            StudentPopUp sp = new StudentPopUp(this, true);
            //true = modal is true = popup takes control of the app
            sp.setLocationRelativeTo(this);//popup over the frame
            sp.setVisible(true);//show the popup 

            Student temp = sp.getStudent();

            String em = temp.validateData();

            if (em == null) {
                s[size] = temp;
                currentstudent = size;
                size++;
                showStudent();
            } else {
                JOptionPane.showMessageDialog(this, em);
            }
        }
        else
            JOptionPane.showMessageDialog(this,"Max of 10 students");

    }//GEN-LAST:event_addbtnActionPerformed

