    private void fillData() {
        String names[] = {"Marge Simpson", "Barnet Gimble", "Whalen Smithers", "Ned Flanders", "Edna Crabapple"};

        int marks[][] = {{75, 85, 72}, {87, 65, 91}, {56, 61, 52}, {47, 35, 51}, {95, 91, 92}};

        for (int i = 0; i < 5; i++) {

            s[i] = new Student(names[i], marks[i]);
            size++;

            System.out.println(s[i].getName() + "\tAverage: " + s[i].getAverage());

        }
        currentstudent = 0;
    }

    public void showStudent() {
        jTextField1.setText(s[currentstudent].getName());
        jTextField2.setText("" + s[currentstudent].getMark(1));
        jTextField3.setText("" + s[currentstudent].getMark(2));
        jTextField4.setText("" + s[currentstudent].getMark(3));
        jLabel7.setText("" + s[currentstudent].getAverage());
        jLabel9.setText("" + size);
        jLabel11.setText("" + (currentstudent + 1));
    }

